import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import './LoginPage.css';

function LoginPage() {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [loginStatus, setLoginStatus] = useState('');
  const [showSignUp, setShowSignUp] = useState(false); // Estado para controlar a exibição do formulário de registro
  const history = useHistory();

  useEffect(() => {
    const pwShowHide = document.querySelectorAll(".eye-icon");
    const links = document.querySelectorAll(".link");

    const handlePasswordVisibility = () => {
      let pwFields = document.querySelectorAll(".password");
      
      pwFields.forEach(password => {
        if(password.type === "password"){
          password.type = "text";
          return;
        }
        password.type = "password";
      })
    };

    const toggleSignUp = () => {
      setShowSignUp(show => !show);
    };

    pwShowHide.forEach(eyeIcon => {
      eyeIcon.addEventListener("click", handlePasswordVisibility);
    });

    links.forEach(link => {
      link.addEventListener("click", toggleSignUp);
    });

    // Cleanup dos event listeners quando o componente é desmontado
    return () => {
      pwShowHide.forEach(eyeIcon => {
        eyeIcon.removeEventListener("click", handlePasswordVisibility);
      });
      links.forEach(link => {
        link.removeEventListener("click", toggleSignUp);
      });
    };
  }, []); // Apenas executado uma vez quando o componente é montado

  const handleSubmit = (event) => {
    event.preventDefault();

    const correctName = 'admin'; // para demonstração
    const correctPassword = '123';

    if (name === correctName && password === correctPassword) {
      setLoginStatus('Login successful!');
      history.push('/map');
    } else {
      setLoginStatus('Login failed: Incorrect name or password.');
    }

    console.log('Logging in:', name, password);
  };

  return (
    <div className="login-page">
      <h2>{showSignUp ? 'Register' : 'Login'}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="password"
          className="password" // Adicione a classe 'password' para identificar os campos de senha
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">{showSignUp ? 'Register' : 'Login'}</button>
      </form>
      {loginStatus && <p>{loginStatus}</p>}
      <div className="link">{showSignUp ? 'Already have an account? Login now' : 'Don\'t have an account? Register now'}</div>
    </div>
  );
}

export default LoginPage;
