import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import './LoginPage.css';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [isLoginForm, setIsLoginForm] = useState(true);
  const history = useHistory();

  const handleSubmit = (event) => {
    event.preventDefault();

    const correctEmail = 'admin@example.com'; // apenas para demonstração
    const correctPassword = '123';

    if (email === correctEmail && password === correctPassword) {
      history.push('/map');
    } else {
      alert('Login failed: Incorrect email or password.');
    }

    console.log('Logging in:', email, password);
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const toggleForm = () => {
    setIsLoginForm(!isLoginForm);
  };

  return (
    <section className="container forms">
      <div className={`form ${isLoginForm ? 'login' : 'signup'}`}>
        <div className="form-content">
          <header>{isLoginForm ? 'Login' : 'Signup'}</header>
          <form onSubmit={handleSubmit}>
            <div className="field input-field">
              <input
                type="email"
                placeholder="Email"
                className="input"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>

            <div className="field input-field">
              <input
                type={showPassword ? 'text' : 'password'}
                placeholder="Password"
                className="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <i
                className={`bx eye-icon ${showPassword ? 'bx-show' : 'bx-hide'}`}
                onClick={togglePasswordVisibility}
              ></i>
            </div>

            {isLoginForm && (
              <div className="form-link">
                <a href="#" className="forgot-pass">Forgot password?</a>
              </div>
            )}

            <div className="field button-field">
              <button type="submit">{isLoginForm ? 'Login' : 'Signup'}</button>
            </div>
          </form>

          <div className="form-link">
            <span>
              {isLoginForm ? "Don't have an account? " : "Already have an account? "}
              <a href="#" className="link" onClick={toggleForm}>{isLoginForm ? 'Signup' : 'Login'}</a>
            </span>
          </div>
        </div>

        <div className="line"></div>

        <div className="media-options">
          <a href="#" className="field facebook">
            <i className='bx bxl-facebook facebook-icon'></i>
            <span>Login with Facebook</span>
          </a>
        </div>

        <div className="media-options">
          <a href="#" className="field google">
            <img src="#" alt="" className="google-img" />
            <span>Login com o Google</span>
          </a>
        </div>
      </div>
    </section>
  );
}

export default LoginPage;
